import React, { Component } from 'react';

class Category extends Component{
	constructor(props){
		super(props);
		this.state = {
			array: []
		}
	}

	apiCall(url, consecuencia){
		
		fetch(url)
		.then(response => response.json())
		.then(data => consecuencia(data))
		.catch(error => console.log(error));

		
	}

	actualizarComponente = (data) =>{
		console.log(data)
		this.setState ({
			array: data
			
		});
	}

	
	componentDidMount(){
		
				
		this.apiCall("http://localhost:3500/api/categories", this.actualizarComponente);
		
	}

	componentDidUpdate(){

	}

	render(){
		
		
		return (
			
			<ul>
				{this.state.array.map(function(item){

					<li>{item.nombre}</li>

				})}

			</ul>
				

			
		)

		

		
	}
}


export default Category;
